import React,{useState} from 'react';
import axios from "axios"
class ToDo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data:[],
    }
  }

  componentDidMount() {
    const token = JSON.parse(localStorage.getItem('login')).token;
    console.log(token)
    axios.get("https://api-nodejs-todolist.herokuapp.com/task",{

      headers:{
        "Authorization":"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MDYzN2VlNTNmYzRjODAwMTdmMDMwNjciLCJpYXQiOjE2MTcxMzMyODV9.KX96p4epsTCfiAixBO0K3EWg_QeaNlrDvgfchGvQSqM",
        "Content-Type":"application/json"
      }
      
          })
    .then(response => {
      console.log(response.data);
      this.setState({data:[...response.data.data]}, () => {
        console.log(this.state.data)
        const {data} = this.state;
        console.log(data);
        console.log(typeof data)
      });
    })
    .catch(error => {
      console.log(error);
    });
  }

  render() {
  
    return (
     
      <div>
       {this.state.data.length > 0 ? 
       <>
       { this.state.data.map(todo => (
          <h3 key={todo._id}>
            {todo.description}
          </h3>
        ))}
        </>
        :
        <h3>No Todos yet .... try adding some </h3>
        }
     
      </div>
    );
  }
}

export default ToDo
























