import React,{useState} from 'react';
import axios from "axios"
import "./ToDo.scss"
class ToDo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data:[],
    }
  }

  componentDidMount() {
    const token = JSON.parse(localStorage.getItem('login')).token;
    axios.get("https://api-nodejs-todolist.herokuapp.com/task",{

      headers:{
        "Authorization":`Bearer ${token}`,
        "Content-Type":"application/json"
      }
      
          })
    .then(response => {
      console.log(response.data);
      this.setState({data:[...response.data.data]}, () => {
        console.log(this.state.data)
        const {data} = this.state;
        console.log(data);
        console.log(typeof data)
      });
    })
    .catch(error => {
      console.log(error);
    });
  }
  componentDidMount() {
    fetch('/api/todolist')
    .then(res => res.json())
    .then((results)=> {    
      console.log(results)
      this.setState({todolist:results})
    })   
}

  deleteTodo(id){

      fetch('/api/todolist/' + id, {
          method: 'DELETE',
          mode: 'CORS'
      }).then(res => res)
  }

  render() {
  
    return (
     
      <div>
    

        <div class="app-container" id="taskList">
  
  <ul class="task-list">
		
		
        {this.state.data.length > 0 ? 
       <>
       { this.state.data.map(todo => (
        <li class="task-list-item" v-for="task in tasks">
			<label class="task-list-item-label">
        <input type="checkbox"/>
        <span key={todo._id}>   
            {todo.description}
            </span>
            </label>
			<span  class="delete-btn" title="Delete Task"></span>
		</li>
        ))}
        </>
        :
  null
        }

       
		
	</ul>
</div>




     
      </div>
    );
  }
}

export default ToDo
























