import React,{useState} from 'react';
import { useHistory } from 'react-router';

function NavigationNavPage(props) {
  const[Text,setText]=useState("");

  const history=useHistory();
 async function SaveTask(){
    console.warn(Text)
    let item={Text}
    let result=await fetch ("https://api-nodejs-todolist.herokuapp.com/task",
    {

      method:'POST',
      headers:{
        "Content-Type": "application/json",
    
        "Authorization":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI2MDA4Y2MzZTVhYjQ2ZjAwMTdiOGY2NGUiLCJpYXQiOjE2MTcxMDA0ODd9.hIXSnr6lcpn0eW-74xdzW7B_S65JEnjfGfTkF1MlBRY"
      },
      body:JSON.stringify(item)
    }
    
    );
    result =await result.json();
    localStorage.setItem("task",JSON.stringify(result))

  };
  async function SaveTask(){
    console.warn(Text)
    let item={Text}
    let result=await fetch ("https://api-nodejs-todolist.herokuapp.com/task",
    {

      method:'GET',
      headers:{
        "Content-Type": "application/json",
    
        "Authorization":"Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJfaWQiOiI1ZGRjY2JlYzZiNTVkYTAwMTc1OTcyMmMiLCJpYXQiOjE1NzQ3NTE2ODh9.GPbsl9FLX4VrsGVErodiXypjuz1us4tfD0jwg2_UrzY"
      },
      body:JSON.stringify(item)
    }
    
    );
    result =await result.json();
    localStorage.setItem("task",JSON.stringify(result))

  };
  return (
    <div>
                
                

    <div className="col-sm-6 offset-sm-3">
    <h1 className="text-center">ToDo List
    
    
    
    </h1>
       <textarea type="text" onChange={(e)=>setText(e.target.value)} className="form-control" />
       <br/>
      
       <button onClick={SaveTask} className="btn btn-primary">Add</button>
       </div>

    </div>
  );
}


export default NavigationNavPage
