  };*/

  deleteTodo(id){
    const token = JSON.parse(localStorage.getItem('login')).token;
    fetch('https://api-nodejs-todolist.herokuapp.com/task/5ddcd1566b55da0017597239' + id, {
        method: 'DELETE',
        headers:{
          "Authorization":`Bearer ${token}`,
          "Content-Type":"application/json"
        },
      
    }).then((res) => 
    {res.json().then((res)=>{
      aler(todo._id)
      args.stopPropagation();
      let liItem = args.target.parentElement.parentElement;
      this.listviewInstance.removeItem(liItem);
    })}
   
    );
}


  render() {
  
    return (
     
      <div>
    

        <div class="app-container" id="taskList">
  
  <ul class="task-list">
		
		
        {this.state.data.length > 0 ? 
       <>
       { this.state.data.map(todo => (
        <li class="task-list-item" v-for="task in tasks">
			<label class="task-list-item-label">
        <input type="checkbox"/>
        <span key={todo._id}>  
         
            {todo.description}
            </span>
            </label>
			<span  class="delete-btn" title="Delete Task"  onClick={this.deleteTodo.bind(this, todo._id)}></span>
		</li>
        ))}
        </>
        :
  null
        }

       
		
	</ul>
</div>




     
      </div>
    );
  }
}

export default ToDo
























