import React,{useState} from 'react';
import { useHistory } from 'react-router';
import ToDo from "./ToDo"
function Todos () {
 {
    return this.props.todos.map(todo => (
      <TodoItem
        key={todo.id}
        todo={todo}
        markComplete={this.props.markComplete}
        delTodo={this.props.delTodo}
        editText={this.props.editText}
        saveText={this.props.saveText}
      />
    ));
  }
}

class TodoItem extends React.Component {
  getStyle = () => {
    return {
      textDecoration: this.props.todo.completed ? "line-through" : "none",
      color: this.props.todo.completed ? "#7a7a7a" : "#000"
    };
  };

  componentDidMount() {
    let todos = Array.from(document.querySelectorAll(".item-wrapper"));
    todos.forEach((todo, i) => {
      todo.style.transitionDelay = i * 0.1 + 0.25 + "s";
    });
  }

  render() {
    const { id, title, completed } = this.props.todo;

    return (
     
    
        <div className="item-wrapper">
          <div className="inner-wrapper">
            <div className="to-do-item">
              <div
                className="toggle-check"
                onClick={this.props.markComplete.bind(this, id)}
              >
                <div
                  className={"line1 line" + (completed ? " trans-line1" : "")}
                />
                <div
                  className={"line2 line" + (completed ? " trans-line2" : "")}
                />
              </div>
              <textarea
                rows="1"
                className="item-content"
                value={title}
                style={this.getStyle()}
                spellCheck="false"
                onChange={this.props.editText.bind(this, id)}
                onBlur={this.props.saveText.bind(this, id)}
              />
            </div>

            <button
              className="del-btn"
              onClick={this.props.delTodo.bind(this, id)}
            >
              <i className="fas fa-trash-alt " />
            </button>
          </div>
        </div>
    
    );
  }
}

function NavigationNavPage(props) {
  const[description, setDescription]=useState("");
  const[todo,setdo]=useState("");
  const history=useHistory();
 async function SaveTask(){
    console.warn(description)
    let item={description}
    const token = JSON.parse(localStorage.getItem('login')).token;

    let result=await fetch ("https://api-nodejs-todolist.herokuapp.com/task",
    {

      method:'POST',
      headers:{
        "Content-Type": "application/json",
        "Accept":"application/json",
        "Authorization":`Bearer ${token}`,
      },
      body:JSON.stringify(item)
    }

    );
    result =await result.json();
    localStorage.setItem("task",JSON.stringify(result))
    setDescription('')

  };


  return (
    <div>
                
                

    <div className="col-sm-6 offset-sm-3">
    <h1 class="app-header">TO DO LIST</h1>
  <div class="add-task">
    <input type="text" autocomplete="off" placeholder="Add New Task" v-model="tasks.name"  onChange={(e)=>setDescription(e.target.value) } className="task-input" />
		<input type="submit" value="" class="submit-task"onClick={SaveTask}  title="Add Task"/>
  </div>
       <textarea type="text" value={description} onChange={(e)=>setDescription(e.target.value)} className="form-control" />
       <br/>
      
       <button onClick={SaveTask} className="btn btn-primary">Add</button>
      <div></div>
       </div>
<ToDo/>
    </div>
  );

  }

export default NavigationNavPage
