import React,{useState} from 'react';
import "./ToDo.css"
import axios from "axios"
class ToDo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data:[],
    }
  }

  componentDidMount() {
    const token = JSON.parse(localStorage.getItem('login')).token;
    axios.get("https://api-nodejs-todolist.herokuapp.com/task",{

      headers:{
        "Authorization":`Bearer ${token}`,
        "Content-Type":"application/json"
      }
      
          })
    .then(response => {
      console.log(response.data);
      this.setState({data:[...response.data.data]}, () => {
        console.log(this.state.data)
        const {data} = this.state;
        console.log(data);
        console.log(typeof data)
      });
    })
    .catch(error => {
      console.log(error);
    });
  }

  render() {
  
    return (
     
      <div>
       {this.state.data.length > 0 ? 
       <>
       { this.state.data.map(todo => (
          <h3 key={todo._id}>
            {todo.description}
          </h3>
        ))}
        </>
        :
        <h3>No Todos yet .... try adding some </h3>
        }
     

        <div class="main-container">
      <h2 class="title">to do list <i class="fas fa-pencil-alt"></i></h2>

      <div class="content">
        <input
          id="inputText"
          type="text"
          placeholder="Add to your list..."
          maxlength="20"
          required
        />
        <i id="addButton" class="fas fa-plus"></i>
        <div class="remove-container">
          <i id="delete-icon" class="far fa-trash-alt"></i>
          <p id="remove-text">Delete All</p>
        </div>
        <ul id="display"></ul>
      </div>

      <div class="completed">
        <h2 class="title-done">
          completed tasks <i class="far fa-check-circle"></i>
        </h2>
        <ul id="display-done"></ul>
      </div>
    </div>





      </div>
    );
  }
}

export default ToDo
























