import { useHistory } from 'react-router';

import { BrowserRouter, Route, Link } from "react-router-dom";


import React, { Component } from 'react';
class AddTodo extends React{
  state = {
    title: ""
  };

  onChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    this.props.addTodo(this.state.title);
    this.setState({
      title: ""
    });
  };

  render() {
    return (
      <form onSubmit={this.onSubmit} style={{ display: "flex" }}>
        <span onClick={this.onSubmit} className="add-btn">
          <i className="fas fa-plus" />
        </span>
        <input
          className="add-todo"
          type="text"
          name="title"
          placeholder="Add item"
          style={{ flex: "10", padding: "5px" }}
          value={this.state.title}
          onChange={this.onChange}
          autoComplete="off"
        />
      </form>
    );
  }
}

class TodoItem extends React {
  getStyle = () => {
    return {
      textDecoration: this.props.todo.completed ? "line-through" : "none",
      color: this.props.todo.completed ? "#7a7a7a" : "#000"
    };
  };

  componentDidMount() {
    let todos = Array.from(document.querySelectorAll(".item-wrapper"));
    todos.forEach((todo, i) => {
      todo.style.transitionDelay = i * 0.1 + 0.25 + "s";
    });
  }

  render() {
    const { id, title, completed } = this.props.todo;

    return (
       <CSSTransitionGroup
        transitionName="example"
        transitionEnterTimeout={0}
        transitionLeaveTimeout={0}
        transitionAppear={true}
        transitionAppearTimeout={0}
      >
    
        <div className="item-wrapper">
          <div className="inner-wrapper">
            <div className="to-do-item">
              <div
                className="toggle-check"
                onClick={this.props.markComplete.bind(this, id)}
              >
                <div
                  className={"line1 line" + (completed ? " trans-line1" : "")}
                />
                <div
                  className={"line2 line" + (completed ? " trans-line2" : "")}
                />
              </div>
              <textarea
                rows="1"
                className="item-content"
                value={title}
                style={this.getStyle()}
                spellCheck="false"
                onChange={this.props.editText.bind(this, id)}
                onBlur={this.props.saveText.bind(this, id)}
              />
            </div>

            <button
              className="del-btn"
              onClick={this.props.delTodo.bind(this, id)}
            >
              <i className="fas fa-trash-alt " />
            </button>
          </div>
        </div>
      </CSSTransitionGroup>
    );
  }
}

class Todos extends React{
  render() {
    return this.props.todos.map(todo => (
      <TodoItem
        key={todo.id}
        todo={todo}
        markComplete={this.props.markComplete}
        delTodo={this.props.delTodo}
        editText={this.props.editText}
        saveText={this.props.saveText}
      />
    ));
  }
}

class ListGroupPage extends React {
  state = {
    todos: []
  };

//retrieves inital data from list storage, in this case from jsonplaceholder
  componentDidMount() {
    axios
      .get("https://jsonplaceholder.typicode.com/todos?_limit=5")
      .then(res => this.setState({ todos: res.data }));
  }

  //custom delay for new todo being added, so that it doesn't keep the incrementing transition delay it has from initial pageload
  componentDidUpdate(prevProps, prevState) {
    let todos = Array.from(document.querySelectorAll(".item-wrapper"));
    let lastEl = todos[prevState.todos.length];
    if (lastEl && prevState.todos.length !== 0) {
      lastEl.style.transitionDelay = 0 + "s";
      lastEl.style.transitionDuration = 0.3 + "s";
    }
  }

  //toggle complete, saves completed items to db
  markComplete = (id, e) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id) {
          todo.completed = !todo.completed;
          axios.put(`https://jsonplaceholder.typicode.com/todos/${id}`, {
            completed: todo.completed
          });
        }
        return todo;
      })
    });
  };

  //update "title" prop in state when user changes text
  editText = (id, e) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id) {
          todo.title = e.target.value;
        }
        return todo;
      })
    });
  };

   //when user clicks outside the item row, data is saved to db
  saveText = (id, e) => {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (todo.id === id) {
          // todo.title = e.target.value;
          axios.put(`https://jsonplaceholder.typicode.com/todos/${id}`, {
            title: todo.title
          });
        }
        return todo;
      })
    });
  };

  //delete todo
  delTodo = id => {
    axios.delete(`https://jsonplaceholder.typicode.com/todos/${id}`).then(res =>
      this.setState({
        todos: [...this.state.todos.filter(todo => todo.id !== id)]
      })
    );
  };

  //create new todo
  addTodo = title => {
    axios
      .post("https://jsonplaceholder.typicode.com/todos/", {
        title,
        completed: false
      })
      .then(res =>
        this.setState({
          todos: [...this.state.todos, res.data]
        })
      );
  };

  render() {
    return (
      <Router>
        <div className="App">
          <div className="container">
            <Header />
            <Route
              exact
              path="/"
              render={props => (
                <React.Fragment>
                  <AddTodo addTodo={this.addTodo} />

                  <div className="todos-wrapper">
                    <Todos
                      todos={this.state.todos}
                      markComplete={this.markComplete}
                      delTodo={this.delTodo}
                      editText={this.editText}
                      saveText={this.saveText}
                    />
                  </div>
                </React.Fragment>
              )}
            />
            <Route path="/about" component={About} />
          </div>
        </div>
      </Router>
    );
  }
}

function About() {
  return (
    <React.Fragment>
      <div className="about-wrapper">
        <h1 className="about-heading">About</h1>
        <h3 className="about-subheading">How To</h3>
        <ul>
          <li>Click on &#10006; / &#10004; to toggle cross-out todo item</li>
          <li>Click on the trash icon to delete a todo item</li>
          <li>
            Write new text in "Add todo" field and either press enter or click
            the &#10010; to add new item
          </li>
          <li>You can edit any item by writing new text in it</li>
          <li>Click anywhere outside the item row to save the new text</li>
        </ul>
        <h3 className="about-subheading">How It Works</h3>
        <p className="about-text">
          App uses the following: React, Axios, JSONplaceholder or MockApi
        </p>
        <p className="about-text">
          Data is updated in JSONplaceholder/MockApi db when todo items are deleted, marked as
